import React from "react";
import { Container } from "react-bootstrap";
import { useState, useRef, useEffect } from "react";
import "./Contact.css";
function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const el = useRef();
  const emailinput = useRef();
  const area = useRef();
  const lb = useRef();
  const emaillb = useRef();
  const arealb = useRef();
  const handleFoucs = () => {
    lb.current.classList.add("show");
    emaillb.current.classList.add("show");
    arealb.current.classList.add("show");
  };
  const handleBlur = () => {
    el.value = "string"
      ? lb.current.classList.add("show")
      : lb.current.classList.remove("show");
    emailinput.value = "string"
      ? emaillb.current.classList.add("show")
      : emaillb.current.classList.remove("show");
    area.value = "string"
      ? arealb.current.classList.add("show")
      : arealb.current.classList.remove("show");
    // if ((el.value = "string")) {
    //     lb.current.classList.add("show");
    //   } else {
    //     lb.current.classList.remove("show");
    // }
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const el = { name, email, message };
    console.log(el);
    setName("");
    setEmail("");
    setMessage("");
  };
  return (
    <Container>
      <div className="contact">
        <h1>Contact Us</h1>
        <form onSubmit={handleSubmit}>
          <div className="text-input">
            <input
              ref={el}
              type="text"
              className="text-input"
              onChange={(e) => setName(e.target.value)}
              onFocus={handleFoucs}
              onBlur={handleBlur}
              name="name"
            />
            <label ref={lb} className="label">
              Name
            </label>
          </div>
          <div className="text-input">
            <input
              ref={emailinput}
              type="text"
              className="text-input"
              onChange={(e) => setEmail(e.target.value)}
              name="email"
            />
            <label ref={emaillb} className="label">
              Email
            </label>
          </div>
          <div className="text-area">
            <textarea
              ref={area}
              className="text-area"
              onChange={(e) => setMessage(e.target.value)}
              name="message"
            />
            <label ref={arealb} className="label">
              Message
            </label>
          </div>
          <button className="send">Send</button>
        </form>
      </div>
    </Container>
  );
}
export default Contact;
