import React from "react";
import { Container } from "react-bootstrap";
import { useState, useRef, useEffect } from "react";
import "./Contact.css";

function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const NameRef = useRef();
  NameRef.current = name;
  const emailRef = useRef();
  const areaRef = useRef();
  const lb = useRef();
  const lbemail = useRef();
  const lbarea = useRef();
  const handleFoucs = (element) => {
    element.current.classList.add("show");
  };
  const handleBlur = (element) => {
    if (NameRef.current == "") {
      element.current.classList.add("show");
    } else {
      element.current.classList.remove("show");
    }
  };
  console.log(NameRef.current);
  const handleSubmit = async (e) => {
    e.preventDefault();
    const el = { name, email, message };
    console.log(el);
    setName("");
    setEmail("");
    setMessage("");
  };
  console.log(NameRef);
  return (
    <Container>
      <div className="contact">
        <h1>Contact Us</h1>
        <form onSubmit={handleSubmit}>
          <div className="text-input">
            <input
              ref={NameRef}
              value={name}
              type="text"
              className="text-input"
              onChange={(e) => setName(e.target.value)}
              onFocus={() => handleFoucs(lb)}
              onBlur={() => handleBlur(lb)}
            />
            <label ref={lb} className="label">
              Name
            </label>
          </div>
          <div className="text-input">
            <input
              ref={emailRef}
              type="text"
              className="text-input"
              onChange={(e) => setEmail(e.target.value)}
              onFocus={() => handleFoucs(lbemail)}
              name="email"
            />
            <label ref={lbemail} className="label">
              Email
            </label>
          </div>
          <div className="text-area">
            <textarea
              ref={areaRef}
              className="text-area"
              onChange={(e) => setMessage(e.target.value)}
              onFocus={() => handleFoucs(lbarea)}
              name="message"
            />
            <label ref={lbarea} className="label">
              Message
            </label>
          </div>
          <button className="send">Send</button>
        </form>
      </div>
    </Container>
  );
}
export default Contact;
