import React from "react";
import { Container } from "react-bootstrap";
import { useState, useRef, useEffect } from "react";
import "./Contact.css";
function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const el = useRef([]);
  el.current = [];
  const lb = useRef([]);
  lb.current = []
  const handleFoucs = () => {
    lb.current.classList.add("show");
  };
  const handleBlur = () => {
    if ((el.value = "string")) {
      lb.current.classList.add("show");
    } else {
      lb.current.classList.remove("show");
    }
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const el = { name, email, message };
    console.log(el);
    setName("");
    setEmail("");
    setMessage("");
  };
  const addToREf = (el) => {
    console.log(el);
    el.onBlur = () =>{
      console.log("hello");
    }
  };
  const addToREflb = (lb) => {
    console.log(lb);
  };
  return (
    <Container>
      <div className="contact">
        <h1>Contact Us</h1>
        <form onSubmit={handleSubmit}>
          <div className="text-input">
            <input
              ref={addToREf}
              type="text"
              className="text-input"
              onChange={(e) => setName(e.target.value)}
              onFocus={handleFoucs}
              onBlur={handleBlur}
              name="name"
            />
            <label ref={addToREflb} className="label">
              Name
            </label>
          </div>
          <div className="text-input">
            <input
              ref={addToREf}
              type="text"
              className="text-input"
              onChange={(e) => setEmail(e.target.value)}
              name="email"
            />
            <label ref={addToREflb} className="label">Email</label>
          </div>
          <div className="text-area">
            <textarea
              ref={addToREf}
              className="text-area"
              onChange={(e) => setMessage(e.target.value)}
              name="message"
            />
            <label ref={addToREflb} className="label">Message</label>
          </div>
          <button className="send">Send</button>
        </form>
      </div>
    </Container>
  );
}
export default Contact;
